package day2;

public class harshini {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		if(n%2==0) {
			if(n>3) {
				System.out.println("num divisible by 2 and greater than 3");
			}
		}
		else {
			if(!(n<3)) {
				System.out.println("num not divisible by 2 and greater than 3");
			}
		}

	}

}
